alter table operacao_interna_financeiro add tipo_titulo_id bigint;alter table operacao_interna_financeiro add grupo_financeiro_id bigint;alter table operacao_interna_financeiro add caracteristica_movimento_financeiro_id bigint;alter table operacao_interna_financeiro add historico_padrao_id bigint;alter table operacao_interna_financeiro add CONSTRAINT tipo_titulo_oper_interna_financeiro_foreign FOREIGN KEY (tipo_titulo_id) REFERENCES tipo_titulo (id);
alter table operacao_interna_financeiro add CONSTRAINT grupo_financeiro_oper_interna_financeiro_foreign FOREIGN KEY (grupo_financeiro_id) REFERENCES grupo_financeiro (id);alter table operacao_interna_financeiro add CONSTRAINT caracter_movto_fin_oper_interna_financeiro_foreign FOREIGN KEY (caracteristica_movimento_financeiro_id) REFERENCES caracteristica_movimento_financeiro (id);alter table operacao_interna_financeiro add CONSTRAINT historico_padrao_oper_interna_financeiro_foreign FOREIGN KEY (historico_padrao_id) REFERENCES historico_padrao (id);