create sequence seq_cfop;

create table cfop (
	id					bigint not null constraint cfop_pkey primary key,
	codigo				int not null,
	nome				varchar(2000) not null,
	destino_operacao		varchar(20) not null,
	data_inicio_vigencia 	date not null,
	data_final_vigencia 	date not null,
	user_create     	bigint 		 not null,
	date_create			timestamp with time zone 	 not null,
	user_update     	bigint,
	date_update			timestamp with time zone
);

