create sequence seq_mensagem_fiscal start with 1;

create table mensagem_fiscal (
	id					bigint not null constraint mensagem_fiscal_pkey primary key,
	codigo				integer not null,
	codigo_ajuste		varchar(20),
	nome				varchar(2000) not null,
	sigla				varchar(30) not null,
	obs_fiscal			varchar(2000),
	uf_id				bigint,
	user_create     	bigint 		 not null,
	date_create			timestamp with time zone 	 not null,
	user_update     	bigint,
	date_update			timestamp with time zone,
	CONSTRAINT mensagem_fiscal_uf_id_foreign FOREIGN KEY (uf_id) REFERENCES uf (id)
);

